import React, { Dispatch, SetStateAction } from "react";
import "./checkGuessButtons.css"; // Import your CSS file for styling
import { Guess } from "../../game-engine/Guess";
import { WordWithPosition } from "..../game-engine/shuffleObject";

interface NytButtonGroupProps {
  words: string[];
  guess: Guess;
  strikes: Guess[];
  idx: number;
  setIsCorrect: Dispatch<SetStateAction<boolean>>;
  setStrikes: Dispatch<SetStateAction<Guess[]>>;
  correctPsns: number[];
  setCorrectPsns: Dispatch<SetStateAction<number[]>>;
}

const NytButtonGroup: React.FC<NytButtonGroupProps> = ({
  guess,
  setIsCorrect,
  strikes,
  setStrikes,
  correctPsns,
  setCorrectPsns,
}) => {
  function moveItemToTop<WordWithPosition>(
    array: WordWithPosition[],
    position: number
  ): WordWithPosition[] {
    if (position < 0 || position >= array.length) {
      // Invalid position, return the original array
      return array;
    }

    const movedItem = array[position];
    const newArray = array.filter((item, index) => index !== position);

    // Insert the moved item at the beginning of the array
    newArray.unshift(movedItem);

    return newArray;
  }

  const onCheck = () => {
    if (guess.v === guess.c) {
      setIsCorrect(true);
      setCorrectPsns([...correctPsns, guess.v]);
    } else {
      setStrikes([...strikes, { v: guess.v, c: guess.c }]);
    }
  };

  const onClear = () => {};

  return (
    <div className="nyt-button-group-container">
      <button className="nyt-button nyt-button-check" onClick={onCheck}>
        Check
      </button>
      <button className="nyt-button nyt-button-clear" onClick={onClear}>
        Clear
      </button>
    </div>
  );
};

export default NytButtonGroup;
