import React, { useState } from "react";

interface MovableBoxProps {
  id: number;
  position: { x: number; y: number };
  onMoveLeft: (id: number) => void;
}

const MovableBox: React.FC<MovableBoxProps> = ({
  id,
  position,
  onMoveLeft,
}) => {
  return (
    <div
      style={{
        position: "absolute",
        top: `${position.y}vh`,
        left: `${position.x}vw`,
        width: "20vw",
        height: "20vh",
        backgroundColor: "lightblue",
        cursor: "pointer",
      }}
      onClick={() => onMoveLeft(id)}
    >
      Box {id}
    </div>
  );
};

// Example usage:
const ReorderBoxesComponent: React.FC = () => {
  const initialBoxPositions = [
    { id: 1, position: { x: 0, y: 0 } },
    { id: 2, position: { x: 25, y: 0 } },
    { id: 3, position: { x: 50, y: 0 } },
    { id: 4, position: { x: 75, y: 0 } },
  ];

  const [boxes, setBoxes] = useState(initialBoxPositions);

  const handleMoveLeft = (clickedId: number) => {
    setBoxes((prevBoxes) => {
      const clickedBoxIndex = prevBoxes.findIndex(
        (box) => box.id === clickedId
      );
      const clickedBox = prevBoxes[clickedBoxIndex];
      const newBoxes = prevBoxes.slice();

      // Move the clicked box to the leftmost position
      newBoxes.splice(clickedBoxIndex, 1);
      newBoxes.unshift(clickedBox);

      return newBoxes;
    });
  };

  return (
    <div
      style={{
        height: "100vh",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
      }}
    >
      {boxes.map((box) => (
        <MovableBox
          key={box.id}
          id={box.id}
          position={box.position}
          onMoveLeft={handleMoveLeft}
        />
      ))}
    </div>
  );
};

export default ReorderBoxesComponent;
