import React, { useState } from 'react';
import './ButtonGroup.css'; // Import your CSS file for styling

interface ButtonGroupProps {
  words: string[]
//   onCheck: () => void;
//   onClear: () => void;
//   isDisabled: boolean; // Add the new prop
}

const ButtonGroup: React.FC<ButtonGroupProps> = ({words}) => {
  const [selectedButton, setSelectedButton] = useState(0);

  const buttonData = [
    { id: 1, label: "Button 1" },
    { id: 2, label: "Button 2" },
    { id: 3, label: "Button 3" },
    // Add more buttons as needed
  ];

  const handleButtonClick = (id: number) => {
    setSelectedButton(id === selectedButton ? 0 : id);
  };

  return (
    <div className="button-group-container">
      {words.map((button, index) => (
        <button
          key={index}
          className={`button ${index === selectedButton ? "selected" : ""}`}
          onClick={() => handleButtonClick(index)}
        >
          {button.label}
        </button>
      ))}
    </div>
  );
};

export default ButtonGroup;
