import React, { useState } from "react";

interface MovableBoxProps {
  position: { x: number; y: number };
  onMoveLeft: () => void;
}

const MovableBox: React.FC<MovableBoxProps> = ({ position, onMoveLeft }) => {
  return (
    <div
      style={{
        position: "absolute",
        top: `${position.y}vh`,
        left: `${position.y}vh`,
        width: "100px",
        height: "100px",
        backgroundColor: "lightblue",
      }}
    >
      <button onClick={onMoveLeft}>Move Left</button>
    </div>
  );
};

// Example usage:
const MoveLeftComponent: React.FC = () => {
  const [boxPosition, setBoxPosition] = useState<{ x: number; y: number }>({
    x: 50,
    y: 100,
  });

  const handleMoveLeft = () => {
    setBoxPosition((prevPosition) => ({
      x: prevPosition.x - 50, // Move 50 pixels to the left
      y: prevPosition.y,
    }));
  };

  return (
    <div
      style={{
        height: "100vh",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
      }}
    >
      <MovableBox position={boxPosition} onMoveLeft={handleMoveLeft} />
    </div>
  );
};

export default MoveLeftComponent;
