interface WordObjectWithPositions {
  word: string;
  originalPosition: number;
}

const shuffleWordObjectsWithPositions = (
  wordObjects: { word: string; position: number }[]
): WordObjectWithPositions[] => {
  const wordObjectsWithPositions: WordObjectWithPositions[] = wordObjects.map(
    ({ word, position }) => ({
      word,
      originalPosition: position
    })
  );

  let currentIndex = wordObjectsWithPositions.length,
    randomIndex;

  // While there remain elements to shuffle.
  while (currentIndex > 0) {
    currentIndex--;
    randomIndex = Math.floor(Math.random() * currentIndex);

    // Swap elements using destructuring assignment
    [
      wordObjectsWithPositions[currentIndex],
      wordObjectsWithPositions[randomIndex],
    ] = [
      wordObjectsWithPositions[randomIndex],
      wordObjectsWithPositions[currentIndex],
    ];
  }

  return wordObjectsWithPositions;
};

interface ShuffledWordObjectWithSplit extends WordObjectWithPositions {
  vowels: string;
  consonants: string;
}

const splitWordIntoVowelsAndConsonants = (
  wordObjects: { word: string; position: number }[]
): WordObjectWithPositions[] => {
  const result: ShuffledWordObjectWithSplit[] = wordObjects.map(
    ({ word, position }) => {
      const vowels = word.match(/[aeiou]/gi)?.join("") || "";
      const consonants = word.match(/[^aeiou]/gi)?.join("") || "";

      return {
        word,
        position,
        vowels,
        consonants,
      };
    }
  );

  return result;
};

export { shuffleWordObjectsWithPositions, splitWordIntoVowelsAndConsonants };

