import React, { useEffect, useState } from "react";
import logo from "./logo.svg";
import ButtonGroup from "./components/buttonGroup";
import "./App.css";
import NytButtonGroup from "./components/checkGuess/checkGuessButtons";
import Guess from "./game-engine/Guess";
import Shuffle from "./game-engine/shuffler";

const words = [
  "even",
  "lint",
  "scared",
  "pieces",
  "doubt",
  "dream",
  "howl",
  "poured",
];

const App = () => {
  const [idx, setIndex] = useState<number>(-1);
  const [guess, setGuess] = useState<Guess>({ v: "", c: "" });
  const [vowels, setVowels] = useState<string[]>([]);
  const [consonants, setConsonants] = useState<string[]>([]);

  useEffect(() => {
    const shuffledVowels = Shuffle(words.map(getVowels));
    setVowels(shuffledVowels);
    const consonantsArray = words.map(getConsonants);
    setConsonants(consonantsArray);
  }, []);

  function getVowels(str: string): string {
    return str.match(/[aeiou]/gi)!.join("");
  }

  function getConsonants(str: string): string {
    return str.match(/[^aeiou]/gi)!.join("");
  }

  console.log(guess);

  return (
    <div className="App">
      <header className="App-header">
        <p>Select two buttons that match</p>
        <ButtonGroup
          words={vowels}
          setGuess={setGuess}
          guess={guess}
          setIndex={setIndex}
          idx={idx}
          isVowel={true}
        />
        <ButtonGroup
          words={consonants}
          setGuess={setGuess}
          guess={guess}
          setIndex={setIndex}
          idx={idx}
          isVowel={false}
        />
        {/* <ButtonGroup words={consonants} onVowelSelect={onVowelSelect} /> */}
        <NytButtonGroup guess={guess}  />
      </header>
    </div>
  );
};

export default App;
