import React, { useState } from "react";

interface MovableBoxProps {
  id: number;
  position: { x: number; y: number };
  onMoveLeft: (id: number) => void;
}

const MovableBox: React.FC<MovableBoxProps> = ({
  id,
  position,
  onMoveLeft,
}) => {
  return (
    <div
      style={{
        position: "absolute",
        top: `${position.y}vh`,
        left: `${position.x}vw`,
        width: "20vw",
        height: "20vh",
        backgroundColor: "lightblue",
        cursor: "pointer",
      }}
      onClick={() => onMoveLeft(id)}
    >
      Box {id}
    </div>
  );
};

// Example usage:
const MoveLeftComponent: React.FC = () => {
  const [boxes, setBoxes] = useState<
    { id: number; position: { x: number; y: number } }[]
  >([
    { id: 1, position: { x: 0, y: 0 } },
    { id: 2, position: { x: 25, y: 0 } },
    { id: 3, position: { x: 50, y: 0 } },
  ]);

  const handleMoveLeft = (clickedId: number) => {
    setBoxes((prevBoxes) => {
      // Find the leftmost box
      const leftmostBox = prevBoxes.reduce((leftmost, box) =>
        box.position.x < leftmost.position.x ? box : leftmost
      );

      // Calculate the new positions for each box
      const newBoxes = prevBoxes.map((box) => ({
        ...box,
        position: {
          x: box.position.x - leftmostBox.position.x,
          y: box.position.y,
        },
      }));

      // Move the clicked box to the leftmost position
      const clickedBoxIndex = newBoxes.findIndex((box) => box.id === clickedId);
      const clickedBox = newBoxes.splice(clickedBoxIndex, 1)[0];
      newBoxes.unshift(clickedBox);

      return newBoxes;
    });
  };

  return (
    <div
      style={{
        height: "100vh",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
      }}
    >
      {boxes.map((box) => (
        <MovableBox
          key={box.id}
          id={box.id}
          position={box.position}
          onMoveLeft={handleMoveLeft}
        />
      ))}
    </div>
  );
};

export default MoveLeftComponent;
