import React, { useEffect, useState } from "react";
import logo from "./logo.svg";
import ButtonGroup from "./components/buttonGroup";
import "./App.css";
import NytButtonGroup from "./components/checkGuess/checkGuessButtons";
import { Guess, GuessHistory } from "./game-engine/Guess";
import Shuffle from "./game-engine/shuffler";
import { WordWithPosition, shuffleVowels, splitWordsIntoVowelsAndConsonants } from "./game-engine/shuffleObject";
import getCorrectList from "./game-engine/getCorrectList";

const words = [
  "even",
  "lint",
  "scared",
  "pieces",
  "doubt",
  "dream",
  "howl",
  "poured",
];

const App = () => {
  const [idx, setIndex] = useState<number>(-1);
  const [guess, setGuess] = useState<Guess>({ v: -1, c: -1 });
  const [strikes, setStrikes] = useState<Guess[]>([]);
  // const [guessHistory, setGuessHistory] = useState<GuessHistory>({});
  const [displayVowels, setDisplayVowels] = useState<WordWithPosition[]>([]);
  const [displayConsonants, setDisplayConsonants] = useState<WordWithPosition[]>([]);
  const [isCorrect, setIsCorrect] = useState<boolean>(false);
  const [correctpositions, setCorrectPsns] = useState<number[]>([]);
  // const correctpositions = [3];

  useEffect(() => {    
    const { vowels, consonants } = splitWordsIntoVowelsAndConsonants(words);
    const shuffledVowels = shuffleVowels(vowels);
    setDisplayVowels(shuffledVowels)
    setDisplayConsonants(consonants)
    // console.log(shuffledVowels, consonants);
  }, []
  );

  useEffect(() => {
    localStorage.setItem("strikes", strikes);
  }, [])

  console.log(guess);

  return (
    <div className="App">
      <header className="App-header">
        <p>found: {getCorrectList(words, correctpositions)}</p>
        <p>strikes: {strikes.length}</p>
        <p>Select two buttons that match</p>
        <ButtonGroup
          words={displayVowels}
          setGuess={setGuess}
          guess={guess}
          setIndex={setIndex}
          idx={idx}
          isVowel={true}
          correct={correctpositions}
        />
        <ButtonGroup
          words={displayConsonants}
          setGuess={setGuess}
          guess={guess}
          setIndex={setIndex}
          idx={idx}
          isVowel={false}
          correct={correctpositions}
        />
        <NytButtonGroup
          words={words}
          guess={guess}
          idx={idx}
          setIsCorrect={setIsCorrect}
          strikes={strikes}
          setStrikes={setStrikes}
          correctPsns={correctpositions}
          setCorrectPsns={setCorrectPsns}
        />
      </header>
    </div>
  );
};

export default App;
